using Akiled.Communication.Packets.Outgoing.Structure;using Akiled.HabboHotel.GameClients;using Akiled.HabboHotel.Items;using Akiled.HabboHotel.Quests;using Akiled.HabboHotel.Rooms;namespace Akiled.Communication.Packets.Incoming.Structure{    class MoveObjectEvent : IPacketEvent    {        public void Parse(GameClient Session, ClientPacket Packet)        {            Room room = AkiledEnvironment.GetGame().GetRoomManager().GetRoom(Session.GetHabbo().CurrentRoomId);            if (room == null || !room.CheckRights(Session))                return;            Item roomItem = room.GetRoomItemHandler().GetItem(Packet.PopInt());            if (roomItem == null)                return;            if (room.RoomData.SellPrice > 0)
            {
                Session.SendNotification(AkiledEnvironment.GetLanguageManager().TryGetValue("roomsell.error.7", Session.Langue));
                return;
            }            int newX = Packet.PopInt();            int newY = Packet.PopInt();            int newRot = Packet.PopInt();            Packet.PopInt();            if (newX != roomItem.GetX || newY != roomItem.GetY)                AkiledEnvironment.GetGame().GetQuestManager().ProgressUserQuest(Session, QuestType.FURNI_MOVE, 0);            if (newRot != roomItem.Rotation)                AkiledEnvironment.GetGame().GetQuestManager().ProgressUserQuest(Session, QuestType.FURNI_ROTATE, 0);            if (roomItem.GetZ >= 0.1)                AkiledEnvironment.GetGame().GetQuestManager().ProgressUserQuest(Session, QuestType.FURNI_STACK, 0);            if (!room.GetRoomItemHandler().SetFloorItem(Session, roomItem, newX, newY, newRot, false, false, true))            {                room.SendPacket(new ObjectUpdateComposer(roomItem, roomItem.OwnerId));                return;            }        }    }}