using Akiled.HabboHotel.GameClients;using Akiled.HabboHotel.Items;using Akiled.HabboHotel.Quests;using Akiled.HabboHotel.Rooms;namespace Akiled.Communication.Packets.Incoming.Structure{    class UseFurnitureEvent : IPacketEvent    {        public void Parse(GameClient Session, ClientPacket Packet)        {            Room room = AkiledEnvironment.GetGame().GetRoomManager().GetRoom(Session.GetHabbo().CurrentRoomId);            if (room == null)                return;            int pId = Packet.PopInt();            Item roomItem1 = room.GetRoomItemHandler().GetItem(pId);            if (roomItem1 == null)                return;            if (roomItem1.GetBaseItem().ItemName == "bw_lgchair")                AkiledEnvironment.GetGame().GetQuestManager().ProgressUserQuest(Session, QuestType.EXPLORE_FIND_ITEM, 1936);            else if (roomItem1.GetBaseItem().ItemName.Contains("bw_sboard"))                AkiledEnvironment.GetGame().GetQuestManager().ProgressUserQuest(Session, QuestType.EXPLORE_FIND_ITEM, 1969);            else if (roomItem1.GetBaseItem().ItemName.Contains("bw_van"))                AkiledEnvironment.GetGame().GetQuestManager().ProgressUserQuest(Session, QuestType.EXPLORE_FIND_ITEM, 1956);            else if (roomItem1.GetBaseItem().ItemName.Contains("party_floor"))                AkiledEnvironment.GetGame().GetQuestManager().ProgressUserQuest(Session, QuestType.EXPLORE_FIND_ITEM, 1369);            else if (roomItem1.GetBaseItem().ItemName.Contains("party_ball"))                AkiledEnvironment.GetGame().GetQuestManager().ProgressUserQuest(Session, QuestType.EXPLORE_FIND_ITEM, 1375);            else if (roomItem1.GetBaseItem().ItemName.Contains("jukebox"))                AkiledEnvironment.GetGame().GetQuestManager().ProgressUserQuest(Session, QuestType.EXPLORE_FIND_ITEM, 1019);            else if (roomItem1.GetBaseItem().ItemName.Contains("bb_gate"))                AkiledEnvironment.GetGame().GetQuestManager().ProgressUserQuest(Session, QuestType.EXPLORE_FIND_ITEM, 2050);            else if (roomItem1.GetBaseItem().ItemName == "bb_patch1")                AkiledEnvironment.GetGame().GetQuestManager().ProgressUserQuest(Session, QuestType.EXPLORE_FIND_ITEM, 2040);            else if (roomItem1.GetBaseItem().ItemName == "bb_rnd_tele")                AkiledEnvironment.GetGame().GetQuestManager().ProgressUserQuest(Session, QuestType.EXPLORE_FIND_ITEM, 2049);            else if (roomItem1.GetBaseItem().ItemName.Contains("es_gate_"))                AkiledEnvironment.GetGame().GetQuestManager().ProgressUserQuest(Session, QuestType.EXPLORE_FIND_ITEM, 2167);            else if (roomItem1.GetBaseItem().ItemName.Contains("es_score_"))                AkiledEnvironment.GetGame().GetQuestManager().ProgressUserQuest(Session, QuestType.EXPLORE_FIND_ITEM, 2172);            else if (roomItem1.GetBaseItem().ItemName.Contains("es_exit"))                AkiledEnvironment.GetGame().GetQuestManager().ProgressUserQuest(Session, QuestType.EXPLORE_FIND_ITEM, 2166);            else if (roomItem1.GetBaseItem().ItemName == "es_tagging")                AkiledEnvironment.GetGame().GetQuestManager().ProgressUserQuest(Session, QuestType.EXPLORE_FIND_ITEM, 2148);            bool UserHasRights = false;            if (room.CheckRights(Session))                UserHasRights = true;            string str = roomItem1.ExtraData;            int Request = Packet.PopInt();            roomItem1.Interactor.OnTrigger(Session, roomItem1, Request, UserHasRights);            roomItem1.OnTrigger(room.GetRoomUserManager().GetRoomUserByHabboId(Session.GetHabbo().Id));        }    }}