using Akiled.HabboHotel.GameClients;using Akiled.HabboHotel.Items;using Akiled.HabboHotel.Rooms;namespace Akiled.Communication.Packets.Incoming.Structure{    class UpdateStickyNoteEvent : IPacketEvent    {        public void Parse(GameClient Session, ClientPacket Packet)        {            Room room = AkiledEnvironment.GetGame().GetRoomManager().GetRoom(Session.GetHabbo().CurrentRoomId);            if (room == null || !room.CheckRights(Session))                return;            Item roomItem = room.GetRoomItemHandler().GetItem(Packet.PopInt());            if (roomItem == null || roomItem.GetBaseItem().InteractionType != InteractionType.POSTIT)                return;            string str1 = Packet.PopString();            string str2 = Packet.PopString();            if (!room.CheckRights(Session) && !str2.StartsWith(roomItem.ExtraData))                return;            switch (str1)            {                case "FFFF33":                case "FF9CFF":                case "9CCEFF":                case "9CFF9C":                    roomItem.ExtraData = str1 + " " + str2;                    roomItem.UpdateState(true, true);                    break;            }        }    }}