using Akiled.HabboHotel.GameClients;namespace Akiled.HabboHotel.Rooms.Chat.Commands.Cmd{    class Kick : IChatCommand    {        public void Execute(GameClient Session, Room Room, RoomUser UserRoom, string[] Params)        {            Room currentRoom = Session.GetHabbo().CurrentRoom;            GameClient clientByUsername = AkiledEnvironment.GetGame().GetClientManager().GetClientByUsername(Params[1]);            if (clientByUsername == null || clientByUsername.GetHabbo() == null)                Session.SendNotification(AkiledEnvironment.GetLanguageManager().TryGetValue("input.usernotfound", Session.Langue));            else if (Session.GetHabbo().Rank <= clientByUsername.GetHabbo().Rank)                Session.SendNotification(AkiledEnvironment.GetLanguageManager().TryGetValue("action.notallowed", Session.Langue));            else if (clientByUsername.GetHabbo().CurrentRoomId < 1U)            {                Session.SendNotification(AkiledEnvironment.GetLanguageManager().TryGetValue("kick.error", Session.Langue));            }            else            {                Room.GetRoomUserManager().RemoveUserFromRoom(clientByUsername, true, false);                if (Params.Length > 2)                {                    string message = CommandManager.MergeParams(Params, 2);                    if (Session.Antipub(message, "<CMD>", Room.Id))                        return;                    clientByUsername.SendNotification(AkiledEnvironment.GetLanguageManager().TryGetValue("kick.withmessage", clientByUsername.Langue) + message);                }                else                    clientByUsername.SendNotification(AkiledEnvironment.GetLanguageManager().TryGetValue("kick.nomessage", clientByUsername.Langue));            }        }    }}