using Akiled.Communication.Packets.Outgoing.Structure;
using Akiled.HabboHotel.GameClients;using System.Linq;

namespace Akiled.HabboHotel.Rooms.Chat.Commands.Cmd{    class Massbadge : IChatCommand    {        public void Execute(GameClient Session, Room Room, RoomUser UserRoom, string[] Params)        {            string Badge = Params[1];            if (string.IsNullOrEmpty(Badge))                return;            foreach (GameClient Client in AkiledEnvironment.GetGame().GetClientManager().GetClients.ToList())            {                if (Client.GetHabbo() != null)                {                    Client.GetHabbo().GetBadgeComponent().GiveBadge(Badge, 0, true);                    Client.SendPacket(new ReceiveBadgeComposer(Badge));
                    Client.SendMessage(new NewYearComposer(Badge));                }            }        }    }}