using Akiled.HabboHotel.GameClients;namespace Akiled.HabboHotel.Rooms.Chat.Commands.Cmd{    class setspeed : IChatCommand    {        public void Execute(GameClient Session, Room Room, RoomUser UserRoom, string[] Params)        {            Room currentRoom = Session.GetHabbo().CurrentRoom;            if (currentRoom == null)                return;            if (!currentRoom.CheckRights(Session, true))                return;            try            {                Session.GetHabbo().CurrentRoom.GetRoomItemHandler().SetSpeed(int.Parse(Params[1]));            }            catch            {                UserRoom.SendWhisperChat(AkiledEnvironment.GetLanguageManager().TryGetValue("input.intonly", Session.Langue));            }        }    }}