using Akiled.HabboHotel.GameClients;using System;namespace Akiled.HabboHotel.Rooms.Chat.Commands.Cmd{    class trigger : IChatCommand    {        public void Execute(GameClient Session, Room Room, RoomUser UserRoom, string[] Params)        {            Room TargetRoom = Session.GetHabbo().CurrentRoom;            if (TargetRoom == null)                return;            if (Params.Length != 2)                return;            RoomUser TargetRoomUser = TargetRoom.GetRoomUserManager().GetRoomUserByHabbo(Convert.ToString(Params[1]));            if (TargetRoomUser == null)                return;            if (TargetRoomUser.GetClient().GetHabbo().Id == Session.GetHabbo().Id)                return;            if (!((Math.Abs((TargetRoomUser.X - UserRoom.X)) >= 2) || (Math.Abs((TargetRoomUser.Y - UserRoom.Y)) >= 2)))            {                TargetRoom.UserCmd(TargetRoomUser);            }        }    }}